import React, { useEffect, useState } from 'react'
import './TextFeild.css'

function TextFeild({headerText, bodyText}) {
    const[addClass, setAddClass] = useState()
    const myRef = React.useRef(null)
    const[distInView, setDistInview] = useState()
    
    useEffect(() => {
        setDistInview(myRef.current.getBoundingClientRect().top + 10 - window.innerHeight)
    },)

function FadeIn() {
    function fadeIn() {
        if (distInView < 0) {
            setAddClass(inView)
        } else {
            setAddClass(outOfView)
        }
    }

    window.addEventListener('scroll', fadeIn)

    const inView = {
        opacity: '1',
        paddingTop: '0',
        transition: '1s ease-in-out'
    }

    const outOfView = {
        marginTop: '50px',
        opacity: '0'
    }
  return (
    <div>
    <div className='contain' ref={myRef}>
        <div style={addClass}>
            <div style={{...header}}>
                {headerText}
            </div>
            <div style={{...body}}>
                {bodyText}
            </div>
        </div>
    </div>
    </div>
  )
}}

export default FadeIn
