import React, { useState } from 'react';
import images from "../Utilities/ImageDirectories/CampusWatchImages.json"

function ImageTextTransition() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isMovingRight, setIsMovingRight] = useState(false);
  const [isMovingLeft, setIsMovingLeft] = useState(false);

  

  const handleMoveRight = () => {
    setIsMovingLeft(true);
    setTimeout(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
      setIsMovingLeft(false);
    }, 500);
  };

  const handleMoveLeft = () => {
    setIsMovingRight(true);
    setTimeout(() => {
      setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
      setIsMovingRight(false);
    }, 500);
  };

  return (
    <div className=" w-screen flex items-center justify-center bg-neutral-500">
      <img
        src={images[currentIndex].url}
        alt="Image"
        className={`h-11/12 p-8 sm:w-1/2 w-screen ${
          isMovingRight ? 'translate-y-full opacity-0' : ''
        } ${
          isMovingLeft ? '-translate-y-full opacity-0' : ''
        } transition-all duration-500`}
      />
      <div
        className={`w-1/2 pl-10 ${
          isMovingRight ? 'translate-y-full opacity-0' : ''
        } ${
          isMovingLeft ? '-translate-y-full opacity-0' : ''
        } transition-all duration-500`}
      >
        <p>{images[currentIndex].text} hello boy</p>
      </div>
      <button
        onClick={handleMoveRight}
        className="absolute left-0 bg-white px-4 py-2 rounded-lg"
      >
        Previous
      </button>
      <button
        onClick={handleMoveLeft}
        className="absolute right-0 bg-white px-4 py-2 rounded-lg"
      >
        Next
      </button>
    </div>
  );
}

export default ImageTextTransition;
