import React, {useState, useEffect} from 'react';
import images from '../Utilities/ImageDirectories/16TeamsPhotos.json'
import { Link } from 'react-router-dom';
import { ArrowLeft } from 'react-bootstrap-icons';

function App() {
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    setLoaded(true);
  }, []);

  return (
    <div>
        <div className='w-screen h-16 bg-white fixed p-4 opacity-100 z-10'>
            <Link to={'/homepage'} className='z-10'><ArrowLeft className='w-8 h-8'/></Link>
        </div>
        <div className="h-screen w-screen">
        <div className="h-screen w-full flex justify-center items-center">
            <img
            src="images/Carousel_Images/16_team_photos/PXP-9969.jpg"
            alt="Large Photo"
            className={`${
                loaded ? 'opacity-100' : 'opacity-0'
            } transition-opacity duration-1000`}
            style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover',
            }}
            />
        </div>
        <div className="h-1/2 w-full p-4 grid grid-cols-2 gap-4">
            {images.map((image, index) => (
            <div key={index} className="relative group">
                <img
                src={image.url}
                alt={image.alt}
                className="w-full h-full object-cover transition-opacity duration-300 group-hover:opacity-75"
                />
                <div className="absolute bottom-0 left-0 w-full text-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                <p className="text-neutral-200 text-2xl">{image.text}</p>
                <a href={image.link} className="text-blue-500 text-sm">
                    View More
                </a>
                </div>
            </div>
            ))}
        </div>
        </div>
    </div>
  );
}

export default App;
