import React, {useState, useEffect} from 'react';
import { Link} from 'react-router-dom';
import { ArrowLeft, XLg } from 'react-bootstrap-icons';

function App({title, id, ImagesPath, bigImage, buttonName}) {
  const [loaded, setLoaded] = useState(false);
    const[expanded, setExpanded] = useState(normal)

const normal = {
    width: '100%',
    height: '100%',
    transition: '1s ease-in-out'
}

const enlarge = {
    width: "100vw",
    height: "100vh",
    transition: '1s ease-in-out'
}
    const expand = () => {
        setExpanded(enlarge)
    }

  useEffect(() => {
    setLoaded(true);
  }, []);

  return (
    <div className='w-screen h-auto' >
        <div className='w-screen h-16 bg-white fixed p-4 opacity-100 z-10'>
            <Link to={'/Gallery'} className='z-10'><ArrowLeft className='w-8 h-8'/></Link>
        </div>
        <div className="h-auto w-screen">
        <div className="h-screen w-full flex justify-center items-center">
            <img id={id}
            src={bigImage}
            alt="Large img"
            className={`${
                loaded ? 'opacity-100' : 'opacity-0'
            } transition-opacity duration-1000`}
            style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover',
            }}
            />
            <div
            className={`${
                loaded ? 'translate-y-0' : 'translate-y-24'
            } transition-transform duration-1000 absolute left-1/2 text-center transform -translate-x-1/2 -translate-y-1/2 sm:text-3xl text-lg text-white font-bold`}
            >
            {title}
            </div>
        </div>
        <div className="h-1/2 w-full p-4 grid grid-cols-2 gap-4">
            <>
            
            </>
        </div>
        </div>
    </div>
  );
}

export default App;
