import React, { useState, useEffect } from 'react';
const Indexbackground = "images/church_images/PXP-1451.jpg"

function App() {
  const [textPosition, setTextPosition] = useState('bottom');

  useEffect(() => {
    const intervalId = setTimeout(() => {
      setTextPosition('middle');
    }, 1000); // adjust the delay to your liking
    return () => clearTimeout(intervalId);
  }, []);

  return (
    <div className="h-screen flex justify-center items-center bg-cover bg-center">
      <img
        src={Indexbackground}
        alt="Image"
        className="w-full h-full object-cover"
      />
      <p
        className={`text-3xl font-bold text-white absolute ${
          textPosition === 'bottom' ? 'bottom-0' : 'top-1/2'
        } left-1/2 transform -translate-x-1/2 ${
          textPosition === 'middle' ? 'transition-all duration-1000' : ''
        }`}
        style={{
          textDecoration: 'none',
        }}
        onMouseEnter={(e) => (e.target.style.textDecoration = 'underline')}
        onMouseLeave={(e) => (e.target.style.textDecoration = 'none')}
      >
        This is some text
      </p>
    </div>
  );
}

export default App;
