import React from 'react'

function Box({header, body, detail}) {

  function change(){
    body = detail
  }
  return (
    <div className='w-96 h-64 bg-cyan-400 hover:text-white hover:bg-cyan-900 shadow-lg rounded-2xl p-8 m-3 border-neutral-200 border-2' hover={change}>
        <header className='text-3xl'>
          {header}
        </header>
        <div className='pt-16'>
          {body}
        </div>
    </div>
  )
}

export default Box
