import React from 'react'
import 'react-slideshow-image/dist/styles.css'
import { Fade, Zoom, Slide, } from 'react-slideshow-image'
import { ArrowRightSquareFill, ArrowLeftSquareFill, List } from 'react-bootstrap-icons'
import './Slider.css'

const Image1 = 'images/PXP-1291.jpg'
const Image2 = 'images/PXP-0906.jpg'
function Slider() {
    const Images = [
        {
          url: Image1,
          header: "GOD'S VINEYARD GOSPEL TOURNAMENT 2024",
        },
        {
          url: Image2,
        }
    ]
    const divStyle = {
        display: 'flex',
        width: '100%',
        height: '100vh',
        backgroundSize: 'cover'
      }
      const header = {
        paddingLeft: '10px',
        fontSize: '40px',
        color: 'white',
        width: '300px',
        
        backgroundColor: 'royalblue',
        display: 'flex',
        justifyContent: 'space-between'
      }
      const Navigators = {
        backgroundColor: 'transparent',
        color: 'transparent'
      }

  return (
    <div>
      hello
        <div>
          <Slide nextArrow={<ArrowRightSquareFill className='Arrows'/>} prevArrow={<ArrowLeftSquareFill className='Arrows'/>}>
            
          {Images.map((image, index) =>(
              <div key={index}>
                <div style={{...divStyle, backgroundImage: `url(${image.url})`}}>
                <div style={{...header}}>
                    {Images[index].header}
                </div>
                </div>
              </div>
            ))}
          </Slide>
        </div>
    </div>
  )
}

export default Slider
