import React, { useState } from 'react';
import images from "../Utilities/ImageDirectories/CampusWatchImages.json"

function Outreaches() {
  const [currentPage, setCurrentPage] = useState(0);
  const handleNextPage = () => {
    setCurrentPage((prevPage) => (prevPage + 1) % images.length);
  };

  const handlePrevPage = () => {
    setCurrentPage((prevPage) => (prevPage - 1 + images.length) % images.length);
  };

  return (
    <div>
        <h1>Outreaches</h1>
        <div className="relative h-screen w-screen overflow-hidden">
            <img
                src={images[currentPage].url}
                alt="Carousel image"
                className="absolute top-0 left-0 h-full w-full object-cover"
                className
            />
            <div
                className={`absolute top-1/2 left-1/2 text-center -translate-x-1/2 -translate-y-1/2 ${
                currentPage % 2 === 0 ? 'opacity-100' : 'opacity-0'
                }`}
            >
                {images[currentPage].text}
            </div>
            <button
                onClick={handlePrevPage}
                className="absolute top-1/2 left-5 -translate-y-1/2 bg-white px-4 py-2 rounded-lg"
            >
                Prev
            </button>
            <button
                onClick={handleNextPage}
                className="absolute top-1/2 right-5 -translate-y-1/2 bg-white px-4 py-2 rounded-lg"
            >
                Next
            </button>
            </div>
    </div>
  );
}

export default Outreaches;
