import React, { useState, useEffect } from 'react';
const Indexbackground = "images/church_images/PXP-1399.jpg"

function App() {
  const [opacity, setOpacity] = useState(0);
  const [transform, setTransform] = useState('translateY(100%)');

  useEffect(() => {
    const intervalId = setTimeout(() => {
      setOpacity(1);
      setTransform('translateY(0)');
    }, 500); // adjust the delay to your liking
    return () => clearTimeout(intervalId);
  }, []);

  return (
    <div
      className="h-screen flex justify-center items-center bg-cover bg-center"
      style={{
        backgroundImage: `url(${Indexbackground})`,
      }}
    >
      <p
        className="sm:text-6xl hover:text-underline text-2xl text-center font-bold text-white"
        style={{
          opacity: opacity,
          transform: transform,
          transition: 'all 1s ease-in-out',
        }}
      >
        GOD'S VINEYARD GOSPEL FELLOWSHIP
      </p>
    </div>
  );
}

export default App;
