import Slider from "rc-slider"
import FadeIn from "../Utilities/FadeIn"

const image = 'images/church_images/PXP-1394.jpg'

const Arm = () => {
  return (
    <div>
      <div className="flex flex-row justify-start sm:p-16 p-4 flex-wrap">
      <img src={image} className="sm:w-2/5 w-screen rounded-lg w-screen w-screen h-auto"/>
      <div className="pl-24 h-auto text-white flex flex-col justify-around p-8">
        <div className="text-4xl hover:underline p-4" id>PASTORIAL</div>
        <div className="text-4xl hover:underline p-4">MINISTERIAL</div>
        <div className="text-4xl hover:underline p-4">PROPHECIES</div>
      </div>
      </div>
    </div>
  )
}
function AboutUs() {

  return (
    <div className="bg-amber-950 w-screen h-screen flex flex-col justify-center" id="about">
      <h1 className="text-white text-6xl text-center pb-8">Ministry Arms</h1>
      <FadeIn bodyText={<Arm/>}/>
    </div>
  )
}

export default AboutUs
