import React, { useState, useEffect } from 'react';

function App() {
  const [textPosition, setTextPosition] = useState('bottom');

  useEffect(() => {
    const intervalId = setInterval(() => {
      setTextPosition('middle');
    }, 1000); // adjust the time to your liking
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div
      className="h-screen flex justify-center items-center bg-cover bg-center"
      style={{
        backgroundImage: '',
      }}
    >
      <p
        className={`text-3xl font-bold transition-all duration-500 ${
          textPosition === 'middle' ? 'text-center' : 'text-bottom'
        } text-white`}
        style={{
          position: 'absolute',
          bottom: textPosition === 'bottom' ? '0' : '50%',
          transform: textPosition === 'middle' ? 'translateY(-50%)' : '',
        }}
      >
        <span
          className="hover:underline"
          onMouseEnter={(e) => (e.target.style.textDecoration = 'underline')}
          onMouseLeave={(e) => (e.target.style.textDecoration = 'none')}
        >
          Text moving from bottom to middle on page load
        </span>
      </p>
    </div>
  );
}

export default App;
