import React from 'react'
import 'react-slideshow-image/dist/styles.css'
import { Fade, Zoom, Slide, } from 'react-slideshow-image'
import { ArrowRightSquareFill, ArrowLeftSquareFill, List } from 'react-bootstrap-icons'
import './Slider.css'

const Image1 = 'images/PXP-0878.jpg'
const Image2 = 'images/PXP-0906.jpg'
function Slider() {
    const Images = [
        {
          url: Image1,
        },
        {
          url: Image2,
        }
    ]
    const divStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        flexDirection: 'column',
        width: '100%',
        height: '100vh',
        backgroundSize: 'cover'
      }
      const header = {
        margin: '10px',
        fontSize: '50px',
        color: 'white',
        display: 'flex',
        justifyContent: 'space-between'
      }
      const Navigators = {
        backgroundColor: 'transparent',
        color: 'transparent'
      }

  return (
    <div>
      hello
        <div>
          <Slide nextArrow={<div ><ArrowRightSquareFill className='Arrows'/></div>} prevArrow={<div><ArrowLeftSquareFill className='Arrows'/></div>}>
            
          {Images.map((image, index) =>(
              <div key={index}>
                <div style={{...divStyle, backgroundImage: `url(${image.url})`}}>
                  
                  
                </div>
              </div>
            ))}
          </Slide>
        </div>
    </div>
  )
}

export default Slider
