import React, {useState, useEffect} from 'react';
import images from '../Utilities/ImageDirectories/TeamImageDirectory/Teams.json'
import { Link, Route } from 'react-router-dom';
import { ArrowLeft } from 'react-bootstrap-icons';
import { useLocation } from 'react-router-dom';
import TournamentPhotos from '../Utilities/TournamentPhotos'
import BigImages from '../Utilities/ImageDirectories/16TeamsPhotos.json'
// ... other imports

function App({ title }) {
  // ... other code

  const [smallImageStyle, setSmallImageStyle] = useState({
    width: 'auto',
    height: 'auto',
  });

  const [expandedImage, setExpandedImage] = useState(null);

  const Expand = (image) => {
    setExpandedImage(image);
    setSmallImageStyle({
      width: '100vw',
      height: '100vh',
      position: 'fixed',
      top: 0,
      left: 0,
      zIndex: 1000,
    });
  };

  const CloseExpandedImage = () => {
    setSmallImageStyle({
      width: 'auto',
      height: 'auto',
    });
    setExpandedImage(null);
  };

  return (
    <div className="w-screen h-auto">
      {/* ... other code */}

      <div className="h-1/2 sm:w-full w-screen p-4 grid grid-cols-2 gap-4">
        {newImageArray.map((image, index) => (
          <div key={index} className="relative group">
            <img
              src={image.url}
              alt={image.alt}
              className="w-full h-full object-cover transition-opacity duration-300 group-hover:opacity-75"
              style={expandedImage === image ? smallImageStyle : {}}
              onClick={() => Expand(image)}
            />
            {/* ... other code */}
          </div>
        ))}
      </div>

      {expandedImage && (
        <div
          className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 flex justify-center items-center"
          onClick={CloseExpandedImage}
        >
          <img
            src={expandedImage.url}
            alt={expandedImage.alt}
            className="max-w-full max-h-full object-contain"
          />
        </div>
      )}
    </div>
  );
}

export default App;
