import React, { useState } from 'react';
import images from "../Utilities/ImageDirectories/CampusWatchImages.json"

function ImageTextTransition() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isMovingUp, setIsMovingUp] = useState(false);
  const [isMovingDown, setIsMovingDown] = useState(false);

  

  const handleMoveUp = () => {
    setIsMovngDown(true);
    setTimeout(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
      setIsDown(false);
    }, 500);
  };

  const handleMoveDown = () => {
    setIsMovingDown(true);
    setTimeout(() => {
      setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
      setIsMovingDown(false);
    }, 500);
  };

  return (
    <div className="h-screen w-screen flex items-center justify-center">
      <img
        src={images[currentIndex].url}
        alt="Image"
        className={`h-11/12 p-8 sm:w-1/2 w-screen ${
          isMovingUp ? 'translate-y-full opacity-0' : ''
        } ${
          isMovingDown ? '-translate-y-full opacity-0' : ''
        } transition-all duration-500`}
      />
      <div
        className={`w-1/2 pl-10 ${
          isMovingUp ? 'translate-y-full opacity-0' : ''
        } ${
          isMovingDown ? '-translate-y-full opacity-0' : ''
        } transition-all duration-500`}
      >
        <p>{images[currentIndex].text} hello boy</p>
      </div>
      <button
        onClick={handleMoveUp}
        className="absolute left-0 bg-white px-4 py-2 rounded-lg"
      >
        Move Up
      </button>
      <button
        onClick={handleMoveDown}
        className="absolute right-0 bg-white px-4 py-2 rounded-lg"
      >
        Move Down
      </button>
    </div>
  );
}

export default ImageTextTransition;
