import React from 'react'
import { Fade, Zoom, Slide, } from 'react-slideshow-image'
import { ArrowRightSquareFill, ArrowLeftSquareFill, List } from 'react-bootstrap-icons'
import Image1 from '../Pages/images/PXP-0878.jpg'
import './Slider.css'

function Slider() {
    const Images = [
        {
          url: Image1,
        },
        {
            url: Image1,
        },{
            url: Image1,
        }
    ]
    const divStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        flexDirection: 'column',
        height: '100vh',
        backgroundSize: 'cover'
      }
      const header = {
        margin: '10px',
        fontSize: '50px',
        color: 'white',
        display: 'flex',
        justifyContent: 'space-between'
      }
      const Navigators = {
        backgroundColor: 'transparent',
        color: 'transparent'
      }

  return (
    <div>
        <div>
          <Slide nextArrow={<ArrowRightSquareFill className='Arrows'/>} prevArrow={<ArrowLeftSquareFill className='Arrows'/>}>
            
            {ImageArray.map((image, index) =>(
              <div key={index}>
                <div style={{...divStyle, backgroundImage: `url(${Images[index].url})`}}>
                  <div style={{...header}}>
                    {ImageArray[index].header}
                    <List/>
                  </div>
                  
                </div>
              </div>
            ))}
          </Slide>
        </div>
    </div>
  )
}

export default Slider
