import React, {useState, useEffect} from 'react';
import { Link} from 'react-router-dom';
import { ArrowLeft } from 'react-bootstrap-icons';

function App({title, id, ImagesPath, bigImage, buttonName}) {
    const [enlarged, setEnlarged] = useState()

    const handleImageClick = () => {
        setEnlarged(!enlarged);
    };

  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    setLoaded(true);
  }, []);

  return (
    <div className='w-screen h-auto' >
        <div className='w-screen h-16 bg-white fixed p-4 opacity-100 z-10'>
            <Link to={'/Gallery'} className='z-10'><ArrowLeft className='w-8 h-8'/></Link>
        </div>
        <div className="h-auto w-screen">
        <div className="h-screen w-full flex justify-center items-center">
            <img id={id}
            src={bigImage}
            alt="Large img"
            className={`${
                loaded ? 'opacity-100' : 'opacity-0'
            } transition-opacity duration-1000`}
            style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover',
            }}
            />
            <div
            className={`${
                loaded ? 'translate-y-0' : 'translate-y-24'
            } transition-transform duration-1000 absolute left-1/2 text-center transform -translate-x-1/2 -translate-y-1/2 sm:text-3xl text-lg text-white font-bold`}
            >
            {title}
            </div>
        </div>
        <div className="h-1/2 w-full p-4 grid grid-cols-2 gap-4">
            {ImagesPath.map((image, index) => (
            <div key={index} className="relative group">
                {
                    image.link ? (
                        <Link to={`/${image.link}`}>
                            <img
                            src={image.url}
                            alt={image.alt}
                            className="w-full h-full object-cover transition-opacity duration-300 group-hover:opacity-75"
                            />
                            <div className="absolute bottom-0 left-0 w-full text-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                            <div>Visit the Event</div>
                            <div className='w-auto h-auto bg-neutral-900'><p className="text-white text-2xl">{image.teamName}</p></div>
                            </div>
                        </Link>
                    ) : (
                        <>
                            <div>
      <Slider {...settings}>
        {images.map((image, index) => (
          <div key={index}>
            <img
              src={image.src}
              alt={image.alt}
              style={{
                width: enlarged && currentImage === index ? '50%' : '20%',
                borderRadius: '10px',
                boxShadow: enlarged && currentImage === index ? '0 0 20px rgba(0, 0, 0, 0.5)' : 'none',
                zIndex: enlarged && currentImage === index ? 1 : 0,
                position: enlarged && currentImage === index ? 'absolute' : 'relative',
                transition: 'all 0.5s ease-in-out',
              }}
              onClick={() => handleImageClick(index)}
            />
          </div>
        ))}
      </Slider>
      {enlarged && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
          onClick={() => setEnlarged(false)}
        >
          <img
            src={images[currentImage].src}
            alt={images[currentImage].alt}
            style={{
              width: '50%',
              borderRadius: '10px',
              boxShadow: '0 0 20px rgba(0, 0, 0, 0.5)',
            }}
          />
        </div>
      )}
    </div>
                        </>
                    )
                }
            </div>
            ))}
        </div>
        </div>
    </div>
  );
}

export default App;
