import React from 'react'
import 'react-slideshow-image/dist/styles.css'
import { Fade, Zoom, Slide, } from 'react-slideshow-image'
import { ArrowRightSquareFill, ArrowLeftSquareFill, List } from 'react-bootstrap-icons'
import './Slider.css'
import { Link } from 'react-router-dom'

const Image1 = 'images/PXP-1291.jpg'
const Image2 = 'images/PXP-0950.jpg'

function Slider() {
    const Images = [
        {
          url: Image1,
          header: "GOD'S VINEYARD GOSPEL TOURNAMENT 2024",
          buttonName: "Visit the tournament",
        },
        {
          url: Image2,
        }
    ]
    const divStyle = {
        display: 'flex',
        justifyContent: 'center',
        flexDirection: 'column',
        width: '100%',
        height: '100vh',
        backgroundSize: 'cover'
      }
      const header = {
        paddingLeft: '10px',
        fontSize: '40px',
        color: 'white',
        display: 'flex',
        justifyContent: 'space-between'
      }
      const Navigators = {
        backgroundColor: 'transparent',
        color: 'transparent'
      }

      const eventbutton = {
        color: 'black',
      }

  return (
    <div>
      hello
        <div>
          <Fade nextArrow={<ArrowRightSquareFill className='Arrows'/>} prevArrow={<ArrowLeftSquareFill className='Arrows'/>}>
            
          {Images.map((image, index) =>(
              <div key={index}>
                
                <div style={{...divStyle, backgroundImage: `url(${image.url})`}}>
                <Link style={{...eventbutton}}>
                  {image.buttonName}
                </Link>
                  <div style={{backgroundColor: 'black',marginTop: '550px', opacity: 0.7, display: 'flex', justifyContent: 'center'}}>
                    <div style={{...header}}>
                      {Images[index].header}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </Fade>
        </div>
    </div>
  )
}

export default Slider
